// Ensure data array
let data = items[0].json;
if (!Array.isArray(data)) {
  // If n8n wrapped it or returned object
  if (data.data && Array.isArray(data.data)) data = data.data;
  else data = [data];
}

// Transform
return data.map(item => ({
  json: {
    title: item.title || '',
    city: item.city || '',
    district: item.district || '',
    ward: item.ward || '',
    price_million: (item.rent_vnd_month || 0) / 1000000,
    area_m2: item.area_m2 || 0,
    rent_per_sqm_million: (item.rent_vnd_m2 || 0) / 1000000,
    lat: item.lat || 0,
    lon: item.lon || 0,
    type: item.type || '',
    segment: item.segment || '',
    frontage: item.frontage || null,
    floors: item.floors || null,
    views: item.views || 0,
    saved_count: item.savedCount || 0,
    image_url: item.image_url || item.primary_image_url || '',
    raw_data: JSON.stringify(item)
  }
}));
