const data = require('/data/files/vn_rental_3cities_verified.json');

const body = $input.item.json.body;
const listing_id = body.listing_id;
const user_intent = body.user_intent || '';

const listing = data.find(item => item.listing_id === listing_id);

if (!listing) {
  return [{
    json: {
      error: true,
      message: 'Listing not found',
      listing_id
    }
  }];
}

const prompt = `Bạn là chuyên gia tư vấn BĐS. Khách hàng xem mặt bằng:\n\n- ID: ${listing.listing_id}\n- Địa điểm: ${listing.ward}, ${listing.district}, ${listing.province}\n- Loại: ${listing.market_segment}\n- Diện tích: ${listing.area}m²\n- Mặt tiền: ${listing.frontage}m\n- Tầng: ${listing.floors}\n- Giá: ${listing.rental_price.toLocaleString()} VNĐ/tháng\n- Mô tả: ${listing.description.substring(0, 200)}\n\nMục đích: ${user_intent}\n\nĐánh giá (max 250 từ):\n1. Ưu điểm (2-3)\n2. Nhược điểm (2-3)\n3. Khuyến nghị: highly_recommended/recommended/consider/not_suitable\n4. Lý do`;

return [{
  json: {
    listing,
    user_intent,
    prompt
  }
}];

const listing = $('Load Listing').item.json.listing;
const text = $input.item.json.message?.content || '';

let verdict = 'consider';
const lower = text.toLowerCase();
if (lower.includes('highly') || lower.includes('rất phù hợp')) {
  verdict = 'highly_recommended';
} else if (lower.includes('recommended') || lower.includes('phù hợp')) {
  verdict = 'recommended';
} else if (lower.includes('not') || lower.includes('không phù hợp')) {
  verdict = 'not_suitable';
}

return [{
  json: {
    success: true,
    listing_id: listing.listing_id,
    ai_powered: text.length > 0,
    model: text.length > 0 ? 'gemini-1.5-flash' : '',
    verdict,
    analysis: text || 'Mặt bằng có vị trí thuận tiện. Hãy xem trực tiếp.',
    pros: ['Vị trí tốt'],
    cons: ['Cần khảo sát']
  }
}];

const load = $('Load Listing').item.json;

if (load.error) {
  return [{
    json: {
      success: false,
      error: load.message,
      listing_id: load.listing_id
    }
  }];
}

return [{
  json: {
    success: true,
    listing_id: load.listing.listing_id,
    ai_powered: false,
    fallback_reason: 'Gemini error',
    verdict: 'consider',
    analysis: 'Mặt bằng có vị trí thuận tiện. Hãy xem trực tiếp để đánh giá chi tiết.',
    pros: ['Vị trí tốt'],
    cons: ['Cần khảo sát thực tế']
  }
}];
