const data = require('/data/files/vn_rental_3cities_verified.json');
const listing_id = $input.item.json.body.listing_id;
const user_intent = $input.item.json.body.user_intent;

const listing = data.find(item => item.listing_id === listing_id);

if (!listing) {
  return [{
    json: {
      success: false,
      error: 'Listing not found',
      listing_id
    }
  }];
}

return [{
  json: {
    listing,
    user_intent
  }
}];

