const geminiResponse = $input.item.json;
const listing = $('Load Listing').item.json.listing;

let ai_powered = false;
let analysis = 'Phân tích cơ bản.';
let verdict = 'consider';
let model = '';
let pros = ['Vị trí thuận tiện'];
let cons = ['Cần đánh giá trực tiếp'];

if (geminiResponse.candidates && geminiResponse.candidates.length > 0) {
  const text = geminiResponse.candidates[0].content.parts[0].text;
  ai_powered = true;
  analysis = text;
  model = 'gemini-1.5-flash';

  const lowerText = text.toLowerCase();
  if (lowerText.includes('highly') || lowerText.includes('rất phù hợp')) {
    verdict = 'highly_recommended';
  } else if (lowerText.includes('recommended') || lowerText.includes('phù hợp')) {
    verdict = 'recommended';
  } else if (lowerText.includes('not') || lowerText.includes('không phù hợp')) {
    verdict = 'not_suitable';
  }
}

return [{
  json: {
    success: true,
    listing_id: listing.listing_id,
    ai_powered,
    model,
    verdict,
    analysis,
    pros,
    cons
  }
}];

const listing = $('Load Listing').item.json.listing;

return [{
  json: {
