const data = require('/data/files/vn_rental_3cities_verified.json');

const district = $input.item.json.district;
const type = $input.item.json.type;
const segment = $input.item.json.segment;
const area = $input.item.json.area;
const frontage = $input.item.json.frontage;
const floors = $input.item.json.floors;

const filtered = data.filter(item => 
  item.district === district &&
  item.type === type &&
  item.market_segment === segment
);

if (filtered.length === 0) {
  return [{
    json: {
      success: false,
      error: 'No matching data found',
      sample_size: 0
    }
  }];
}

const prices = filtered.map(item => item.rental_price).sort((a,b) => a - b);
const p25 = prices[Math.floor(prices.length * 0.25)];
const median = prices[Math.floor(prices.length * 0.5)];
const p75 = prices[Math.floor(prices.length * 0.75)];
const min = Math.min(...prices);
const max = Math.max(...prices);

const areaAdjust = area > 30 ? 1 : area / 30;
const frontageAdjust = frontage > 4 ? 1 : frontage / 4;
const floorAdjust = 1 + ((floors - 1) * 0.15);

const factor = areaAdjust * frontageAdjust * floorAdjust;

const valuation = {
  suggested_price: Math.round(median * factor),
  price_range: {
    min: Math.round(p25 * factor),
    max: Math.round(p75 * factor)
  },
  stats: { p25, median, p75, min, max, sample_size: filtered.length },
  adjustments: { area: areaAdjust, frontage: frontageAdjust, floors: floorAdjust, factor }
};

return [{ json: valuation }];

const valuation = $('Calculate Valuation').item.json;
const geminiResponse = $input.item.json;

let ai_powered = false;
let market_insight = `Khu vực ${valuation.stats.sample_size > 0 ? 'có ' + valuation.stats.sample_size : 'có 0'} tin rao ${$('Extract Parameters').item.json.segment}.`;
let model = '';
let upgrade_suggestions = [];
let negotiation_tips = [];

if (geminiResponse.candidates && geminiResponse.candidates.length > 0) {
  const text = geminiResponse.candidates[0].content.parts[0].text;
  ai_powered = true;
  market_insight = text;
  model = 'gemini-1.5-flash';
  upgrade_suggestions = ['Dựa trên phân tích AI'];
  negotiation_tips = ['Tham khảo chi tiết từ AI'];
}

return [{
  json: {
    success: true,
    valuation: {
      suggested_price: valuation.suggested_price,
      price_range: valuation.price_range,
      stats: valuation.stats,
      adjustments: valuation.adjustments
    },
    ai_insights: {
      ai_powered,
      model,
      market_insight,
      upgrade_suggestions,
      negotiation_tips
    }
  }
}];

const valuation = $('Calculate Valuation').item.json;

if (!valuation.success && valuation.error) {
  return [{
    json: {
      success: false,
      error: valuation.error,
      ai_insights: {
        ai_powered: false,
        market_insight: 'Không đủ dữ liệu để định giá.'
      }
    }
  }];
}

return [{
  json: {
    success: true,
    valuation: {
      suggested_price: valuation.suggested_price,
      price_range: valuation.price_range,
      stats: valuation.stats,
      adjustments: valuation.adjustments
    },
    ai_insights: {
      ai_powered: false,
      fallback_reason: 'Gemini API error',
      market_insight: `Khu vực có ${valuation.stats.sample_size} tin rao tương tự. Giá trung vị thị trường: ${valuation.stats.median.toLocaleString()} VNĐ/tháng.`
    }
  }
}];
