const data = require('/data/files/vn_rental_3cities_verified.json');

const body = $input.item.json.body;
const district = body.district;
const type = body.type;
const segment = body.segment;
const area = body.area || 0;
const frontage = body.frontage || 0;
const floors = body.floors || 1;

const filtered = data.filter(item => 
  item.district === district &&
  item.type === type &&
  item.market_segment === segment
);

if (filtered.length === 0) {
  return [{
    json: {
      error: true,
      message: 'No data found',
      district,
      segment
    }
  }];
}

const prices = filtered.map(item => item.rental_price).sort((a,b) => a - b);
const p25 = prices[Math.floor(prices.length * 0.25)];
const median = prices[Math.floor(prices.length * 0.5)];
const p75 = prices[Math.floor(prices.length * 0.75)];

const areaFactor = area > 30 ? 1 : area / 30;
const frontageFactor = frontage > 4 ? 1 : frontage / 4;
const floorFactor = 1 + ((floors - 1) * 0.15);
const totalFactor = areaFactor * frontageFactor * floorFactor;

const suggested = Math.round(median * totalFactor);

return [{
  json: {
    district,
    province: body.province || '',
    segment,
    area,
    frontage,
    floors,
    suggested_price: suggested,
    price_range: {
      min: Math.round(p25 * totalFactor),
      max: Math.round(p75 * totalFactor)
    },
    stats: {
      p25, median, p75,
      sample_size: filtered.length
    },
    prompt: `Bạn là chuyên gia BĐS Việt Nam. Phân tích mặt bằng ${segment} tại ${district}, ${body.province}:\n\n- Diện tích: ${area}m²\n- Mặt tiền: ${frontage}m\n- Tầng: ${floors}\n- Giá đề xuất: ${suggested.toLocaleString()} VNĐ/tháng\n- Khoảng giá thị trường: ${Math.round(p25*totalFactor).toLocaleString()} - ${Math.round(p75*totalFactor).toLocaleString()} VNĐ\n- ${filtered.length} mẫu tham khảo\n\nĐưa ra (max 300 từ):\n1. Nhận xét giá và vị trí\n2. Gợi ý nâng cấp\n3. Tips thương lượng`
  }
}];

const calc = $('Calculate Stats').item.json;
const geminiText = $input.item.json.message?.content || '';

return [{
  json: {
    success: true,
    valuation: {
      suggested_price: calc.suggested_price,
      price_range: calc.price_range,
      stats: calc.stats
    },
    ai_insights: {
      ai_powered: geminiText.length > 0,
      model: geminiText.length > 0 ? 'gemini-1.5-flash' : '',
      market_insight: geminiText || `Khu vực có ${calc.stats.sample_size} tin rao. Giá trung vị: ${calc.stats.median.toLocaleString()} VNĐ/tháng.`,
      upgrade_suggestions: [],
      negotiation_tips: []
    }
  }
}];

const calc = $('Calculate Stats').item.json;

if (calc.error) {
  return [{
    json: {
      success: false,
      error: calc.message,
      ai_insights: {
        ai_powered: false,
        market_insight: 'Không đủ dữ liệu để định giá.'
      }
    }
  }];
}

return [{
  json: {
    success: true,
    valuation: {
      suggested_price: calc.suggested_price,
      price_range: calc.price_range,
      stats: calc.stats
    },
    ai_insights: {
      ai_powered: false,
      fallback_reason: 'Gemini error',
      market_insight: `Khu vực có ${calc.stats.sample_size} tin rao. Giá trung vị: ${calc.stats.median.toLocaleString()} VNĐ/tháng.`
    }
  }
}];
