// Build dynamic query based on params
const query = $input.first().json.query || {};

let whereClauses = ['1=1'];
let params = [];
let paramIndex = 1;

// City filter (alias: city or province)
if (query.city || query.province) {
  const city = query.city || query.province;
  params.push(`%${city}%`);
  whereClauses.push(`province ILIKE $${paramIndex++}`);
}

// District filter
if (query.district) {
  params.push(`%${query.district}%`);
  whereClauses.push(`district ILIKE $${paramIndex++}`);
}

// Ward filter
if (query.ward) {
  params.push(`%${query.ward}%`);
  whereClauses.push(`ward ILIKE $${paramIndex++}`);
}

// Type filter
if (query.type) {
  params.push(query.type);
  whereClauses.push(`type = $${paramIndex++}`);
}

// Segment filter
if (query.segment) {
  params.push(query.segment);
  whereClauses.push(`market_segment = $${paramIndex++}`);
}

// Price filters
if (query.min_price || query.minPrice) {
  const minP = query.min_price || query.minPrice;
  params.push(parseFloat(minP));
  whereClauses.push(`price_million >= $${paramIndex++}`);
}
if (query.max_price || query.maxPrice) {
  const maxP = query.max_price || query.maxPrice;
  params.push(parseFloat(maxP));
  whereClauses.push(`price_million <= $${paramIndex++}`);
}

// Area filters
if (query.min_area || query.minArea) {
  const minA = query.min_area || query.minArea;
  params.push(parseFloat(minA));
  whereClauses.push(`area_m2 >= $${paramIndex++}`);
}
if (query.max_area || query.maxArea) {
  const maxA = query.max_area || query.maxArea;
  params.push(parseFloat(maxA));
  whereClauses.push(`area_m2 <= $${paramIndex++}`);
}

// Radius search via Haversine (if lat, lon, radius_m provided)
let radiusFilter = '';
if (query.lat && query.lon && query.radius_m) {
  const lat = parseFloat(query.lat);
  const lon = parseFloat(query.lon);
  const radiusKm = parseFloat(query.radius_m) / 1000;

  // Haversine formula in SQL
  radiusFilter = `
    AND (
      6371 * acos(
        cos(radians(${lat})) * cos(radians(latitude)) *
        cos(radians(longitude) - radians(${lon})) +
        sin(radians(${lat})) * sin(radians(latitude))
      )
    ) <= ${radiusKm}`;
}

// Pagination
const limit = parseInt(query.limit) || 50;
const offset = parseInt(query.offset) || 0;

// Build final SQL
const sql = `
SELECT 
  id, name, address, province, district, ward,
  latitude, longitude, type, market_segment,
  area_m2, frontage_m, floors,
  price_million, rent_per_sqm_million,
  views, saved_count,
  primary_image_url,
  ai_suggested_price, ai_potential_score, ai_risk_level,
  posted_at
FROM listings
WHERE ${whereClauses.join(' AND ')}${radiusFilter}
ORDER BY posted_at DESC NULLS LAST
LIMIT ${limit} OFFSET ${offset}
`;

// Count query
const countSql = `SELECT COUNT(*) as total FROM listings WHERE ${whereClauses.join(' AND ')}${radiusFilter}`;

return {
  json: {
    sql: sql,
    countSql: countSql,
    params: params,
    limit: limit,
    offset: offset
  }
};

// Add price label based on district median
const listings = $input.all();
const output = [];

for (const item of listings) {
  const data = item.json;

  // Calculate price label (simple version - compare to ai_suggested_price)
  let priceLabel = 'fair';
  if (data.ai_suggested_price && data.price_million) {
    const ratio = data.price_million / data.ai_suggested_price;
    if (ratio < 0.9) priceLabel = 'cheap';
    else if (ratio > 1.1) priceLabel = 'expensive';
  }

  output.push({
    json: {
      ...data,
      price_label: priceLabel
    }
  });
}

return output;
