const fs = require('fs');
try {
  const raw = fs.readFileSync('/data/listings.json', 'utf8');
  const allData = JSON.parse(raw);
  const mappedData = allData.map(item => ({
    id: item.id,
    name: item.name,
    district: item.district,
    type: item.type,
    price: item.price,
    area: item.area,
    frontage: item.frontage,
    floors: item.floors,
    latitude: item.latitude,
    longitude: item.longitude,
    images: item.images || [],
    address: item.address,
    ward: item.ward,
    amenities: {
      schools: item.amenities_schools || 0,
      offices: item.amenities_offices || 0,
      competitors: item.amenities_competitors || 0
    },
    owner: item.owner || { name: 'N/A', phone: 'N/A' },
    postedAt: item.posted_at,
    savedCount: item.savedCount || 0,
    ai: {
      potentialScore: item.ai_potential_score,
      suggestedPrice: item.ai_suggested_price,
      riskLevel: item.ai_risk_level,
      priceLabel: item.price > 100 ? 'expensive' : (item.price < 50 ? 'cheap' : 'fair')
    },
    views: item.views
  }));

  const query = $input.first().json.query || {};
  let filtered = mappedData;
  if(query.district) filtered = filtered.filter(x => x.district === query.district);
  if(query.type) filtered = filtered.filter(x => x.type === query.type);
  if(query.maxPrice) filtered = filtered.filter(x => x.price <= parseInt(query.maxPrice));
  if(query.id) filtered = filtered.filter(x => x.id === query.id);

  const limit = parseInt(query.limit) || 50;
  filtered = filtered.slice(0, limit);

  return [{json: {success: true, count: filtered.length, data: filtered}}];
} catch (e) {
  return [{json: {success: false, error: e.message}}];
}

