const fs = require('fs');
let allData = [];
try {
  const raw = fs.readFileSync('/data/listings.json', 'utf8');
  allData = JSON.parse(raw);
} catch(e) { allData = []; }

const body = $input.first().json.body || {};
const { district, area, frontage, floors, type } = body;

// 1. loc du lieu so sanh (Comparables)
let comps = allData.filter(x => x.district === district && x.type === type);
if (comps.length < 3) comps = allData.filter(x => x.district === district);

// 2. Tinh toan chi so thi truong (Market Metrics)
let avgPricePerM2 = 0;
let avgFrontage = 4;
// Gia mac dinh phong ho truong hop ko co du lieu
const defaultBase = {'Hoàn Kiếm':120,'Ba Đình':100,'Đống Đa':75}[district] || 60;

if (comps.length > 0) {
  const totalM2Price = comps.reduce((sum, item) => sum + (item.price / (item.area || 50)), 0);
  avgPricePerM2 = totalM2Price / comps.length;
  avgFrontage = comps.reduce((sum, item) => sum + (item.frontage || 4), 0) / comps.length;
} else {
  avgPricePerM2 = defaultBase / 50;
}

// 3. Cong thuc Dinh gia (Valuation Formula)
let estPrice = (area || 50) * avgPricePerM2;
// Premium Mat tien: +5% cho moi m vuot so voi trung binh
if (frontage) estPrice += estPrice * ((frontage - avgFrontage) * 0.05);
// Premium So tang: +10% cho moi tang them
if (floors > 1) estPrice += estPrice * ((floors - 1) * 0.10);

estPrice = Math.round(estPrice * 10) / 10;

// 4. Tinh Diem Tiem Nang (Demand-based Scoring)
let score = 60;
if (comps.length > 0) {
  // Khu vuc nhieu nguoi xem -> Diem cao
  const totalViews = comps.reduce((sum, item) => sum + (item.views || 0), 0);
  const avgViews = totalViews / comps.length;
  score = Math.min(100, Math.floor((avgViews / 3000) * 80) + 20);
}

return [{json:{
  success:true,
  valuation: {
    suggestedPrice: estPrice,
    priceRange: {min: Math.floor(estPrice*0.9), max: Math.ceil(estPrice*1.1)},
    potentialScore: score,
    riskLevel: score>80?'low':(score>50?'medium':'high'),
    priceLabel: 'fair',
    factors: [
      {name:'Dữ liệu thị trường',impact:'high',note:`Dựa trên ${comps.length} BĐS tương đồng`},
      {name:'Vị trí',impact:'high',note:district},
      {name:'Mặt tiền',impact:frontage > avgFrontage ? 'positive' : 'neutral',note: `${frontage}m (TB: ${avgFrontage.toFixed(1)}m)`}
    ]
  }
}}];

const body = $input.first().json.body || {};
const { monthlyRent, productPrice, dailyCustomers, operatingCost } = body;
const revenue = productPrice * dailyCustomers * 30;
const cost = monthlyRent + (operatingCost||0);
const profit = revenue - cost;
const breakEven = Math.ceil(monthlyRent / (productPrice * dailyCustomers));
const roi = ((profit/cost)*100).toFixed(1);
return [{json:{
  success:true,
  analysis: {
    monthlyRevenue: revenue,
    totalMonthlyCost: cost,
    monthlyProfit: profit,
    breakEvenDays: breakEven,
    roi: parseFloat(roi),
    recommendation: profit>0 ? 'Có lãi - Khả thi' : 'Lỗ - Cần cân nhắc'
  }
}}];
